stations = pd.DataFrame(data)

# Get the current time
current_time = datetime.now().time()


def find_next_arrival():
    current_station = station_combobox.get()

    if current_station in stations['Station_Name'].values:
        station_row = stations[stations['Station_Name'] == current_station]
        arrival_times = station_row['Arrival_Times'].iloc[0]

        # Convert the string arrival times to datetime.time objects
        arrival_times = [datetime.strptime(time, '%H:%M').time() for time in arrival_times]

        # Find the next arrival time
        next_arrival = None  # Initialize next_arrival to None

        for arrival_time in arrival_times:
            if current_time < arrival_time:
                next_arrival = datetime.combine(datetime.now().date(), arrival_time)
                break

        if next_arrival is not None:
            messagebox.showinfo("Next Arrival Time", f"Next train arrives at: {next_arrival.strftime('%H:%M')}")
        else:
            messagebox.showinfo("Next Arrival Time", "No more trains today")
    else:
        messagebox.showerror("Error", "Station not found in the database.")


# Create a GUI window
root = tk.Tk()
root.title("Train Arrival Times")

# Create a label
label = ttk.Label(root, text="Select your current station:")
label.pack(pady=10)

# Create a combobox to select the current station
station_combobox = ttk.Combobox(root, values=stations['Station_Name'].tolist())
station_combobox.pack()

# Create a button to find the next arrival time
find_button = ttk.Button(root, text="Find Next Arrival Time", command=find_next_arrival)
find_button.pack(pady=10)

# Start the GUI main loop
root.mainloop()